		else
		{
			$('#updateActivityModal').find("input[name='allDay']").removeAttr("checked");
		}
		$('#updateActivityModal').find("input[name='title']").val(event.title);
		highlight_event();

		start = getDate(event.start);
		end = getDate(event.end);
