		base_model.set('taskCollection', taskCollection.collection);

		// Update UI
		if (base_model.has("owner_id"))
			$("div[id='list-tasks-" + base_model.get("heading") + "-" + base_model.get("owner_id") + "']").html(taskCollection.render(true).el);
