/**
 * changes the timer value to 00:00:00 format
 */
function secToColonFormat(time)
{
	var friendlyTime = null;
	var min = 0;
	var hours = 0;
	var sec = 0;
	if (time >= 3600)
	{
		hours = Math.floor(time / 3600);
		time = time - hours * 3600;
		if (hours < 10)
		{
			hours = "0" + hours;
		}
		friendlyTime = hours;
	}

	if (time >= 60)
	{
		min = Math.floor(time / 60);
		time = time - min * 60;
	}
	if (min < 10)
	{
		min = "0" + min;
	}
	if (friendlyTime != null)
	{
		friendlyTime += ":" + min;
	}
	else
	{
		friendlyTime = min;
	}

	sec = time;
	if (sec < 10)
	{
		sec = "0" + sec;
	}

	if (friendlyTime != null)
	{
		friendlyTime += ":" + sec;
	}
	else
	{
		friendlyTime = "00:" + sec;
	}

	return friendlyTime;
}

/**
 * provided time it will change it to 0sec format
 * 
 * @param time
 * @returns {String}
 */
function SecondsToCampaignTime(time)
{
	if (time == 0)
		return "0 sec";
	var hours = Math.floor(time / 3600);
	if (hours > 0)
		time = time - hours * 60 * 60;
	var minutes = Math.floor(time / 60);
	var seconds = time - minutes * 60;
	var friendlyTime = "";
	if (hours == 1)
		friendlyTime = hours + " hour";
	if (hours > 1)
		friendlyTime = hours + " hour";
	if (minutes > 0)
		friendlyTime += minutes + " min";
	if (seconds > 0)
		friendlyTime += seconds + " sec";
	if (friendlyTime != "")
		return friendlyTime;
}

