//Get rule from csrules array by rulecount
function getRule(callscriptPrefsJson,ruleCount)
{
	var rules = callscriptPrefsJson.csrules;
	
	for(var i=0;i<rules.length;i++)
		{		
		 if( rules[i].rulecount == ruleCount)
			 {
			   return rules[i];
			 }
		}
}

