angular.module('builder').factory('preview', ['$rootScope', 'dom', 'project', function($rootScope, dom, project) {
    var preview = {

        iframe: $rootScope.previewFrame,

        /**
         * Show preview iframe.
         *
         * @return void
         */
        show: function() {
            var self = preview;

            if ( ! self.iframe) {
                self.iframe  = $('<iframe id="preview-frame"></iframe>').insertAfter('#viewport');
                self.doc     = self.iframe[0].contentWindow.document;
                self.builder = $('#viewport');
                self.closer  = $('#preview-closer');
            }

            self.load(dom.getHtml(null, true, true, project.activePage));

            //add base tag so we can load assets with relative paths
            $(self.doc).find('head').prepend('<base href="//">');

            //self.handlePreviews();
            self.handleLinks();

            self.iframe.removeClass('hidden');
            self.builder.css('right', -self.builder.width());
            self.closer.removeClass('hidden');
        },

        /**
         * Hide preview iframe.
         *
         * @return void
         */
        hide: function() {
            var self = this;
            self.builder.css('right', 0);

            setTimeout(function() {
                self.iframe.addClass('hidden');
                self.closer.addClass('hidden');

                if (self.doc) {
                    self.doc.open();
                    self.doc.write('');
                    self.doc.close();
                }
            }, 300);
        },

        /**
         * Load given html into preview iframe.
         *
         * @param  string html
         * @return void
         */
        load: function(html) {
            this.doc.open('text/html', 'replace');
            this.doc.write(dom.previewsToHtml(html));
            this.doc.close();
        },

        /**
         * Handle user clicks on links in preview iframe.
         *
         * @return void
         */
        handleLinks: function() {
            $(this.doc).find('a').off('click').on('click', function(e) {
                e.preventDefault();

                var href = e.currentTarget.getAttribute('href');

                //if it's a url to hash just bail
                if (href.indexOf('#') > -1)
                {
                    return;
                }

                //if it's an absolute url confirm and continue
                else if (href.indexOf('//') > -1)
                {
                    alertify.confirm('This might navigate you away from the builder, are you sure you want to continue?', function(confirmed) {
                        if (confirmed) {
                            window.location = href;
                        }
                    });
                }

                //otherwise it's a builder page and we'll need to load that
                //pages contents into preview iframe on link click
                else {
                    var pageName = href.replace('.html', '').toLowerCase();

                    for (var i = 0; i < project.active.pages.length; i++) {
                        var page = project.active.pages[i];

                        if (page.name.toLowerCase() == pageName) {
                            preview.load(dom.getHtml(page, true, true));
                            preview.handleLinks();
                        }
                    }
                }
            });
        }

    };

    return preview;
}]);
angular.module('builder').controller('BuilderController', ['$scope', '$rootScope', '$translate', 'bootstrapper', 'elements', 'settings', 'grid', 'preview', 'themes', 'fonts', 'panels', function($scope, $rootScope, $translate, bootstrapper, elements, settings, grid, preview, themes, fonts, panels) {
    $scope.themes = themes;
    $scope.settings = settings;
    $scope.fonts = fonts;

    $('#view').removeClass('loading');

    $scope.bootstrapper = bootstrapper;
    bootstrapper.start();

    $scope.closePreview = function() {
        preview.hide();
    };

    /**
     * Whether or not passed in attribute is editable
     * on the currently active DOM node.
     *
     * @param  {string} prop
     * @return boolean
     */
    $scope.canEdit = function(prop) {

        if ( ! $scope.selected.node) {
            return true;
        } else {
            return $scope.selected.element && $scope.selected.element.canModify.indexOf(prop) !== -1;
        }
    };

    $scope.openPanel = function(name) {
        $rootScope.activePanel = name;
        $rootScope.flyoutOpen = true;
    };

    /**
     * Reposition select or hover boxes on the screen.
     *
     * @param  {string}  name
     * @param  {mixed}   node
     * @param  {mixed}   el
     *
     * @return void
     */
    $rootScope.repositionBox = function(name, node, el) {

        //hide context boxes depending on user settings
        if (! settings.get('enable'+name.ucFirst()+'Box')) {
            return $scope[name+'Box'].hide();
        }

        if (! node) {
            node = $scope.selected.node;
        }

        if (node && node.nodeName == 'BODY') {
            return $scope[name+'Box'].hide();
        }

        if (! el) {
            el = $scope.selected.element;
        }

        if (! el) return true;

        if (name == 'select') {
            $scope.hoverBox.hide();
        }

        var rect = node.getBoundingClientRect();

        if ( ! rect.width || ! rect.height) {
            $scope[name+'Box'].hide();
        } else {
            $scope[name+'Box'].css({
                top: rect.top,
                left: rect.left - 7 + $scope.frameOffset.left - $scope.elemsContWidth,
                height: rect.height,
                width: rect.width
            }).show();

            $scope[name+'BoxTag'].textContent = $translate.instant(el.name);

            //make sure boxes don't go over the breadcrumbs
            if (rect.top + 39 < 55) {
                $scope[name+'BoxActions'].style.top = 0;
            } else {
                $scope[name+'BoxActions'].style.top = '-27px';
            }
        }
    };

    $rootScope.elementFromPoint = function(x, y) {
        var el = $scope.frameDoc.elementFromPoint(x, y);

        //firefox returns html if body is empty,
        //IE doesn't work at all sometimes.
        if ( ! el || el.nodeName === 'HTML') {
            return $scope.frameBody[0];
        }

        return el;
    };

    /**
     * Set given node as active one in the builder.
     * Wrap calls to this method in $apply to avoid sync problems.
     *
     * @param  {object} node
     * @return void
     */
    $rootScope.selectNode = function(node) {
        if ($scope.rowEditorOpen) { return true; }

        $scope.selecting = true;

        $scope.selected.previous = $scope.selected.node;

        //if we get passed an integer instead of a dom node we'll
        //select a node at that index in the currently stored path
        if (angular.isNumber(node)) {
            node = $scope.selected.path[node].node;
        }

        //if we haven't already stored a reference to passed in node, do it now
        if (node && $scope.selected.node !== node) {
            $scope.selected.node = node;
        }

        //cache some more references about the node for later use
        $scope.selected.element = elements.match($scope.selected.node, 'select', true);
        $scope.selected.parent = $scope.selected.node.parentNode;
        $scope.selected.parentContents = $scope.selected.parent.childNodes;

        //position select box on top of the newly selected node
        $scope.repositionBox('select');

        //whether or not the new node is locked
        $scope.selected.locked = $scope.selected.node.className.indexOf('locked') > -1;
        $scope.selected.isImage = $scope.selected.node.nodeName == 'IMG' &&
        $scope.selected.node.className.indexOf('preview-node') === -1;

        //create an array from all parents of this node
        var parents = $($scope.selected.node).parentsUntil('body').toArray();
        parents.unshift($scope.selected.node);

        //create a path to use for breadcrumbs and css selectors
        $scope.selected.path = $.map(parents.reverse(), function(node) {
            return { node: node, name: elements.match(node).name };
        });

        //whether or not this node is a column
        $scope.selected.isColumn = grid.isColumn($scope.selected.node);

        $scope.frameWindow.focus();

        $rootScope.$broadcast('element.reselected', $scope.selected.node);

        panels.open('inspector');

        setTimeout(function(){
            $scope.selecting = false;
        }, 200);
    };
}]);

angular.module('builder.directives').directive('blColorPicker', ['$parse', '$rootScope', 'inspector', function($parse, $rootScope, inspector) {

    return {
        restrict: 'A',
        link: function($scope, el) {

            //create an input element to instantiate color picker on
            var input = $('<input id="color-picker"/>').prependTo(el);

            //initiate color picker with some predefined colors to choose from
            input.spectrum({
                flat: true,
                palette: colorsForPicker,
                showAlpha: true,
                showPalette: true,
                showInput: true
            });

            //cache values needed for color picker repositioning
            var container  = $('#inspector .sp-container'),
                triggers   = $('.color-picker-trigger'),
                contHeight = container.height(),
                arrow      = $('#color-picker-arrow'),
                bottomEdge = $('#viewport').height();

            $rootScope.colorPickerCont = container.add(arrow).addClass('hidden');

            container.find('.sp-choose').on('click', function(e) {
                $rootScope.colorPickerCont.addClass('hidden');
                return false;
            });

            triggers.on('click', function(e) {
                var top   = $(e.target).offset().top + (e.currentTarget.offsetHeight/2 - 15),
                    model = e.currentTarget.dataset.controls,
                    scope = angular.element(e.currentTarget).scope();

                if ( ! $scope.colorProperty || $scope.colorProperty == model) {
                    $rootScope.colorPickerCont.toggleClass('hidden');
                }

                //hide image/gradient panel
                $('#background-flyout-panel').addClass('hidden');
                $('#background-arrow').hide();

                input.off('move.spectrum').on('move.spectrum', function(e, color) {

                    var rgb = color.toRgb();

                    //if transparency is 0 or 1 convert to hex
                    //format otherwise convert to rgba format
                    if (rgb.a === 0 || rgb.a === 1) {
                        color = color.toHexString();
                    } else {
                        color = color.toRgbString();
                    }

                    //write current color on inspector so we have real time
                    //reflection on element in the DOM
                    scope.$apply(function() {
                        $parse(model).assign(scope, color);
                    });
                });

                var tempTop = top - contHeight/2 + 10;

                //position picker 15px above bottom edge if not enough space normally
                if (bottomEdge < (tempTop + contHeight)) {
                    tempTop = bottomEdge - contHeight - 15;
                }

                //position picker 15px below top edge if not enough space normally
                if (tempTop < 0) {
                    tempTop = 15;
                }

                container.css('top', tempTop);

                arrow.css({
                    display: 'block',
                    left: 309,
                    top:  top
                });

                input.spectrum('set', $parse(model)(scope));

                //set trigger as previous and grab color propperty (background, border-color etc)
                //from targets data attribute so we know what we should apply colors to
                $scope.colorProperty = e.currentTarget.dataset.controls;
                inspector.styles.color.property = $scope.colorProperty;
            });

            //create the command to undo color change on dragstart
            input.on("dragstart.spectrum", function(e, color) {
                inspector.sliding = true;
                $scope.$broadcast($scope.colorProperty+'.slidestart', 'color');
            });

            //add the command to undo manager on dragstop
            input.on("dragstop.spectrum", function() {
                inspector.sliding = false;
                $scope.$broadcast($scope.colorProperty+'.slidestop', 'color');
            });
        }
    }

}]);
