function agile_user_setup(c){$("#loading").hide();var b=gadgets.util.getUrlParameters().pid;var a=c+"&hd="+b;$("#agile_content").html("");$("#agile_content").append('<div class="well well-small agile-one-time-setup" style="margin:0px; border-radius:0px; background-color:#f2f2f2; box-shadow:none; border-bottom:0px; border-left:0px; border-right:0px;"><p>Associate your account - one time setup</p><input id="user_domain" class="input-medium" placeholder="my domain" style="vertical-align:baseline;" type="text" /><span style="font-weight:bold;">.agilecrm.com</span><P style="margin:0px;"><input type="button" value="Associate" onclick=agile_gadget_open_popup("'+a+'") class="btn btn-primary" style="padding:2px 6px 2px;"><span id="notify_user" style="display:none; margin-left:20px; color:indianred;"><i>Please enter your domain.</i></span></P></div>');gadgets.window.adjustHeight()}function agile_gadget_open_popup(c){var a=$("#user_domain");if(a.val()=="my domain"||a.val()==""){$("#notify_user").fadeIn().delay(3000).hide(1);a.focus()}else{c+="&domain="+a.val();a.val("");console.log(c);$(".agile-one-time-setup").hide();$("#loading").show();gadgets.window.adjustHeight();var b=window.open(c,"OpenID","height=400,width=400");finished_interval=setInterval(function(){if(b.closed){clearInterval(finished_interval);var d=new gadgets.Prefs();d.set("agile_user_expire_at","0");d.set("agile_user_popup","");d.set("agile_user_exists","");agile_login()}},100)}};