function agile_user_setup(a){$("#loading").hide();var c=gadgets.util.getUrlParameters().pid;var b=a+"&hd="+c;$("#agile_content").children().eq(1).remove();$("#agile_content").append('<div class="well well-small agile-one-time-setup" style="margin:0 0 5px 5px;"><p>Associate your account - one time setup</p><input type="text" id="user_domain" placeholder="Enter your Domain." style="margin:0 10px 0 0;" /><input type="button" value="Associate" onclick=agile_gadget_open_popup("'+b+'") class="btn btn-primary" style="padding:2px 6px 2px;"><span id="notify_user" style="display:none; margin-left:20px; color:indianred;"><i>Please enter your domain.</i></span></div>');gadgets.window.adjustHeight()}function agile_gadget_open_popup(c){var a=$("#user_domain");if(a.val()=="Enter your Domain"||a.val()==""){$("#notify_user").fadeIn().delay(3000).hide(1);a.focus()}else{c+="&domain="+a.val();a.val("");console.log(c);$(".agile-one-time-setup").hide();$("#loading").show();gadgets.window.adjustHeight();var b=window.open(c,"OpenID","height=400,width=400");finished_interval=setInterval(function(){if(b.closed){clearInterval(finished_interval);var d=new gadgets.Prefs();d.set("agile_user_popup","");d.set("agile_user_exists","");agile_login()}},100)}};